import React, { useState } from 'react';
import './LLoginRegister.css';

function LLoginRegister() {
  const [isLoginFormVisible, setIsLoginFormVisible] = useState(true);

  const handleToggleForm = () => {
    setIsLoginFormVisible(!isLoginFormVisible);
  };

  return (
    <div className="App">
      <div className="form-container">
        <div
          className={`form-card ${
            isLoginFormVisible ? 'left-panel-active' : ''
          }`}
        >
          <h2>Login</h2>
          <form>
            <input type="email" placeholder="Email" />
            <input type="password" placeholder="Password" />
            <button type="submit">Sign In</button>
          </form>
          <button className="toggle-btn" onClick={handleToggleForm}>
            Don't have an account? Sign Up
          </button>
        </div>
        <div
          className={`form-card ${
            !isLoginFormVisible ? 'right-panel-active' : ''
          }`}
        >
          <h2>Register</h2>
          <form>
            <input type="text" placeholder="Username" />
            <input type="email" placeholder="Email" />
            <input type="password" placeholder="Password" />
            <button type="submit">Sign Up</button>
          </form>
          <button className="toggle-btn" onClick={handleToggleForm}>
            Already have an account? Sign In
          </button>
        </div>
      </div>
    </div>
  );
}

export default LLoginRegister;
