import React, { useState } from 'react';
const Prediction = () => {
  const [inputs, setInputs] = useState({
    N: '',
    P: '',
    K: '',
    temperature: '',
    humidity: '',
    ph: '',
    rainfall: '',
  });
  const [prediction, setPrediction] = useState(null);

  const handleChange = (event) => {
    setInputs({ ...inputs, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(inputs),
      });
      const data = await response.json();
      setPrediction(data.prediction);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <p>
        N: <input type="text" name="N" onChange={handleChange} />
      </p>
      <p>
        P: <input type="text" name="P" onChange={handleChange} />
      </p>
      <p>
        K: <input type="text" name="K" onChange={handleChange} />
      </p>
      <p>
        Temperature:{' '}
        <input type="text" name="temperature" onChange={handleChange} />
      </p>
      <p>
        Humidity: <input type="text" name="humidity" onChange={handleChange} />
      </p>
      <p>
        pH: <input type="text" name="ph" onChange={handleChange} />
      </p>
      <p>
        Rainfall: <input type="text" name="rainfall" onChange={handleChange} />
      </p>
      <p>
        <button type="submit">Predict</button>
      </p>
      {prediction && <p>Prediction: {prediction}</p>}
    </form>
  );
};

export default Prediction;
